% Plot ALOHA ACO Phase 3 data% ACO3HYD_Plot.m% Plots time series and/or spectrogram% read and plot ALOHA phase-3 hydrophone data% generate .wav sound file% Fred Duennebier%%% time prior to SEIS data 2011/6/9clear all; fclose all;% if you want a log frequency scale, read in F from metadata files and change NFREQ to 474 and% change freqvector to F %load F.mat;basesamplerate=24000; % Hz  input sample rate% load ALOHAdatacstart=1; % starting sample in the record to be read next%***** CHECK ALL CAPITALIZED VARIABLES BELOW FOR DESIRED VALUESPACK=1; % =0 if file is not packed; =1 if packed (NEW DATA ARE ALL PACKEDDECIMATEBY=1; % decimation factor (usually =1, but make this number larger % if not interested in high frequencies. It will save much time and% storage). If >1, even numbers onlyNFREQ=400; % number of spectral estimates to displayREMOVEMEAN=0; % =1 to remove spectral mean for spetrogram ; =0 not to removeHIGHPASSFREQ= 0000; %Hz removes very hig amplitude low frequencies, for time series plot%  if HIGHPASS == 0, skip itLOWPASSFREQ= 0000; %Hz  if LOWPASSFREQ=0, no lowpass filter   %     HIGHPASSFREQ=4000; LOWPASSFREQ=8000;% Whale whistle bandpass   % IF YOU'RE ONLY INTERESTED IN SIGNALS AT FREQUENCIES BELOW 20 HZ, YOU   % SHOULD BE USING .SEIS FILES AND ASSOCIATED SOFTWARESPECGRAM01=0; % =0 if time series plot - not spectrogram              % =1 IF BOTH TIME SERIES AND SPECTROGRAM              % =2 if only spectrogram            newrate=basesamplerate/DECIMATEBY ; % new sample ratenewnyquist=newrate/2; % new Nyquist frequencydeltaf=newnyquist/NFREQ; % plotted change in freq between spectral estimatesfreqvector=[deltaf/2:deltaf:newnyquist]; % freq scale for plot%% enter list of list of filenames for plotting %% % 2017-04-12T18:29:24.640Z	19.3828333	-155.2765	2.692	3.53	ml	56	45	0.006146	0.11	hv	hv61693171	2017-07-05T18:09:57.040Z	6km SW of Volcano, Hawaii	earthquake	0.12	0.13	0.216	35	reviewed	hv	hv% % 2018-06-08 12:44:39.6	2018-06-08	12:44:39.6	19.4138333	-155.2826667	-0.97	5.2	mw	465.3	323.1	323.1																			% ACO_directory = '/Volumes/HEM_data/ARCHIVE_ACO/HYDR024K/2018/06/08/';%'                                                      ^^^^!^^!^^     'ACO_directory =  '/Users/rhett/Desktop/Seismo/ACO_hydro/'oldfolder = cd(ACO_directory);FILEBASE(1,:)='2018-06-08--12.35';FILEBASE(2,:)='2018-06-08--12.40';FILEBASE(3,:)='2018-06-08--12.45';        T0 = 4*60 + 39.6        EQ = '2018-06-08 12:44:39.6';        latlon = [19.4138333	-155.2826667];        min_start_5 = 40;% % nfiles=length(FILEBASE(:,1));channela=zeros(nfiles*5*60*24000,1); % zero out data vector    %   [length = number of files*5 minutes/file * 60 sec/min * 24000 samp/sec]    nn=0;  % record number    fprintf('...Reading data from %d files\n', nfiles)%     ['READING DATA']% nfiles    fprintf('...ACO_directory: %s\n', ACO_directory );for ifile=1:nfiles % big loop over all files       if PACK==1; % Check if this ia a packed file or old-style larger unpacked                        fid=fopen([FILEBASE(ifile,:) '.HYD24BBpk'],'r'); % for packed files              else            fid=fopen([FILEBASE(ifile,:) '.HYDR24KBB'],'r');       end       % if you get an error here, MatLab can't find your file. Be sure       % that:       %   1) time is correct        %   2) the trailer matches the file's trailer and       %   3) the file is located in Matlab's path      % data format is ,'ieee-le' (that's the letter L) See HYDR24 file format    % documentation        % look before you leap!    if fid == -1;         fprintf('...cannot access file: %s\n',[FILEBASE(ifile,:) '.HYD24BBpk'])        error('File not found !');    end    fprintf('...ACO File: %s\n',FILEBASE(ifile,:) );          while feof(fid) < 1                Record          = fread(fid,1,'uint32','ieee-le');%1                Decimation      = fread(fid,1,'uint8','ieee-le');%2                StartofFile     = fread(fid,1,'uint8','ieee-le');%3                Sync1           = fread(fid,1,'uint8','ieee-le');%4                Sync2           = fread(fid,1,'uint8','ieee-le');%5                Statusbyte1     = fread(fid,1,'uint8','ieee-le');%6                Statusbyte2     = fread(fid,1,'uint8','ieee-le');%7                pad1            = fread(fid,1,'uint8','ieee-le');%8                LeftRightFlag   = fread(fid,1,'uint8','ieee-le');%9                tSec            = fread(fid,1,'uint32','ieee-le');%10                tuSec           = fread(fid,1,'uint32','ieee-le');%11                timecount       = fread(fid,1,'uint32','ieee-le');%12                Year            = fread(fid,1,'int16','ieee-le');%13                yDay            = fread(fid,1,'int16','ieee-le');%14                Hour            = fread(fid,1,'char','ieee-le');%15                Min             = fread(fid,1,'char','ieee-le');%16                Sec             = fread(fid,1,'char','ieee-le');%17                Allignment      = fread(fid,1,'char','ieee-le');%18                sSec            = fread(fid,1,'int16','ieee-le');%19                pad2            = fread(fid,1,'uint8','ieee-le');%20                bits            = fread(fid,1,'uint8','ieee-le');%21 in packed format, size of sample in bits                 nn=nn+1;  % add 1 to records read%                 %                 if nn==1%                     Year, yDay, Hour, Min, Sec, sSec%                 end%                 return%                      if feof(fid)<1            if PACK==1; % if data are in packed format                bitn=['bit' num2str(bits)];  % this is the call in fread for sample bit length                cha=fread(fid,4096,bitn,'ieee-le');            else                cha=fread(fid,4096,'long','ieee-le'); % read data for this record            end                sizecha=size(cha);            if sizecha <4096                cha(sizecha+1:4096)=0; %complete 4096 values  not read in            end             if nn==1 % if this is the first record               firsttimemin=Hour*60+Min;               channela(1:4096)=cha;              else               cstart=4096*(nn-1)+1;               cstop=(nn)*4096;                channela(cstart:cstop)=cha; % add new data to data vector            end               end    end  fclose(fid); end % end of big loop over all files['PREPARING DATA']if DECIMATEBY==1  %1= NO DECIMATION    channelb=channela;else    channelb=decimate(channela,DECIMATEBY);end %Add start mnute of first filestartsec=str2num(FILEBASE(1,13:14))*3600+str2num(FILEBASE(1,16:17))*60;dsec=1/newrate;secvec=[startsec:dsec:5*60*nfiles+startsec+2]; % generate timescale vectorspecvec=secvec;if SPECGRAM01>0; % plot spectrogram   ['PREPARING TO PLOT SPECTROGRAM']%     spectrogram(channelb,4096,0,400,4096)   ['Calculatiing Spectrogram']   windowlen=2^10;  % best if factor of 2   p=zeros(NFREQ,floor(length(channelb)/windowlen));      spec=p;      [spec,f,specvec,p]=...        spectrogram(channelb,windowlen,0,freqvector,newrate,'yaxis');         ['Plotting Spectrogram']         %  p=10*log10(medfilt1(abs(p),3));     p=10*log10(abs(p)); % CONVERT TO  dB    if REMOVEMEAN==1 % remove spectral mean        % meanp=medfilt1(mean(p,2),51); % option to median filter        meanp=mean(p,2); % keep this line to NOT median filter        for ii=1:NFREQ        p(ii,:)=p(ii,:)-meanp(ii); % Remove mean         end    end   % C=[min(min(p(:,1:end-1))):3: max(max(p(:,1:end-1)))];        surf(specvec,freqvector,p,'EdgeColor','none');              axis xy; axis tight; colormap(jet); view(0,90);       xlabel('Time');       ylabel('Frequency (Hz)');        %load smoothingresp;% hydrorespsmoother; HYDRBB response     %hydrorespsmoother=0;  % for HYDRHFend nfig=figure; % change figure number to save spectrogram if SPECGRAM01 <=1 ; % plot time series['PREPARING TO PLOT time series']     if LOWPASSFREQ==0; % no lowpass filter requested         npoles=4; %number of filter poles         % generate hi pass filter%          [bb aa]= butter(npoles,HIGHPASSFREQ/(newrate/2),'high');     elseif HIGHPASSFREQ ==0;  % skip it%          break;     else        npoles=4;        wn=[HIGHPASSFREQ LOWPASSFREQ]/(newrate/2);        [bb aa]= butter(npoles,wn); % generate bandpass filter using bb and aa generated above     end      if HIGHPASSFREQ ==0;          channelc = channelb;      else          channelc=filter(bb,aa,detrend(channelb)); % filter data       end      %divide by 60*24 to change sec to days for Matlab time        secvec=secvec/86400;       if length(channelc)>length(secvec);           secvec=[secvec(1):secvec(length(channelc))];       end      plot(secvec(1:length(channelc)),channelc,'k') % plot black      datetick('x','HH:MM'); % label x axis with hours: minutes   % If a sound file is desired, uncomment the next three lines%     maxxx=max(abs(channelc));%     datavecnorm=channelc/maxxx;%     %   wavwrite(datavecnorm,newrate,32,[FILEBASE(1,:)...%     %   '.wav']); % WRITE A .WAV SOUND FILE  ** CHANGE TITLE**%     audiowrite([FILEBASE(1,:),'.wav'],datavecnorm,newrate);end% fclose allcdMATLAB% 2017-08-19 06:35:51% oldfolder = cd('/Volumes/HEM_data/ARCHIVE_ACO/HYDR024K/2017/08/19/')% FILEBASE(1,:)='2017-08-19--06.30';% FILEBASE(2,:)='2017-08-19--06.35';% FILEBASE(3,:)='2017-08-19--06.40';% FILEBASE(4,:)='2017-08-19--06.45';% FILEBASE(5,:)='2017-08-19--06.50';% FILEBASE(6,:)='2017-08-19--06.55';% '2013-04-13 20:45:22'% % oldfolder = cd('/Volumes/HEM_data/ARCHIVE_ACO/HYDR024K/2013/04/13/')% FILEBASE(1,:)='2013-04-13--20.40';% FILEBASE(2,:)='2013-04-13--20.45';% FILEBASE(3,:)='2013-04-13--20.50';% FILEBASE(4,:)='2013-04-13--20.55';%2019-02-07--19.00.HYD24BBpk% % oldfolder = cd('/Volumes/HEM_data/DATA_ACO/HYDR024K/02/07/')% % FILEBASE(1,:)='2019-02-07--19.00';% % FILEBASE(2,:)='2019-02-07--19.05';% % FILEBASE(3,:)='2019-02-07--19.10';% %